export default (posts = [], action) => {
  switch (action.type) {
    case "FETCH_ALL":
      //state
      return action.payload;
    case "CREATE":
      return [...posts, action.payload];
    case "UPDATE":
      return [...posts, action.payload];

    default:
      return posts;

      break;
  }
};
